mod handler;

use lambda_runtime::{run, service_fn, Error, LambdaEvent};
use aws_lambda_events::{event::sqs::SqsEvent};

#[tokio::main]
async fn main() -> Result<(), Error> {
    tracing_subscriber::fmt()
        .with_max_level(tracing::Level::INFO)
        .with_target(false)
        .without_time()
        .init();
    run(service_fn(catch_all)).await
}

async fn catch_all(event: LambdaEvent<SqsEvent>) -> Result<(), Error> {
    tracing::debug!("{:?}", event);
    match handler::handle(event).await {
        Ok(_) => Ok(()),
        Err(e) => {
            tracing::error!("{:?}", e);
            Err(Error::from(e.to_string()))
        }
    }
}

#[cfg(test)]
mod tests {
    use std::collections::HashMap;
    use std::env;
    use aws_lambda_events::sqs::{SqsEvent, SqsMessage};
    use lambda_runtime::LambdaEvent;
    use super::catch_all;
    use tokio_test::assert_ok;

    #[tokio::test]
    async fn init() {
        env::set_var("TIKI_BUCKET", "mytiki-core-trail-write");
        env::set_var("TIKI_REGION", "us-east-2");
        env::set_var("AWS_PROFILE", "sandbox-mike");
        let body = "{ \"timestamp\": \"2024-03-04T20:32:59+0000\", \"key\": \"\" }";
        
        let event = LambdaEvent {
            payload: SqsEvent { records: vec![
                SqsMessage {
                    message_id: Some("MessageId-1".to_string()),
                    receipt_handle: None,
                    body: Some(body.to_string()),
                    md5_of_body: None,
                    md5_of_message_attributes: None,
                    attributes: HashMap::from([(
                        "MessageGroupId".to_string(),
                        "init:1234".to_string()
                    )]),
                    message_attributes: Default::default(),
                    event_source_arn: None,
                    event_source: None,
                    aws_region: None
                }
            ]},
            context: Default::default(),
        };

        let res = catch_all(event).await;
        assert_ok!(res);
    }
    
    
    #[tokio::test]
    async fn txn() {
        env::set_var("TIKI_BUCKET", "mytiki-core-trail-write");
        env::set_var("TIKI_REGION", "us-east-2");
        env::set_var("AWS_PROFILE", "sandbox-mike");
        let body1 = "{ \"timestamp\": \"2024-03-04T20:32:59+0000\", \"contents\": \"cGxhY2Vob2xkZXIgLSBjb250ZW50cw==\", \"userSignature\": \"cGxhY2Vob2xkZXIgLSB1c2VyU2lnbmF0dXJl\" }";
        let body2 = "{ \"timestamp\": \"2024-03-04T21:32:59+0000\", \"contents\": \"cGxhY2Vob2xkZXIgLSBjb250ZW50cw==\", \"userSignature\": \"cGxhY2Vob2xkZXIgLSB1c2VyU2lnbmF0dXJl\" }";
        
        let event = LambdaEvent {
            payload: SqsEvent { records: vec![
                SqsMessage {
                    message_id: Some("MessageId-1".to_string()),
                    receipt_handle: None,
                    body: Some(body1.to_string()),
                    md5_of_body: None,
                    md5_of_message_attributes: None,
                    attributes: HashMap::from([(
                        "MessageGroupId".to_string(), 
                        "txn:1234:abcd".to_string()
                    )]),
                    message_attributes: Default::default(),
                    event_source_arn: None,
                    event_source: None,
                    aws_region: None 
                },
                SqsMessage {
                    message_id: Some("MessageId-1".to_string()),
                    receipt_handle: None,
                    body: Some(body2.to_string()),
                    md5_of_body: None,
                    md5_of_message_attributes: None,
                    attributes: HashMap::from([(
                        "MessageGroupId".to_string(),
                        "1234:abcd".to_string()
                    )]),
                    message_attributes: Default::default(),
                    event_source_arn: None,
                    event_source: None,
                    aws_region: None
                },
            ], },
            context: Default::default(),
        };
        
        let res = catch_all(event).await;
        assert_ok!(res);
    }
}

